mapper.select:
select a.PROCESSDEFINITION_KEY,d.PROCESS_NAME,ALL_COUNT,case when DONE_COUNT is null then 0 else DONE_COUNT end as DONE_COUNT,(ALL_COUNT-COMP_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 when DONE_COUNT is null then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME, case when TERMINATION_COUNT is null then 0 else TERMINATION_COUNT end as TERMINATION_COUNT from ((select PROCESSDEFINITION_KEY,count(1) as ALL_COUNT,count(END_TIME) as COMP_COUNT,sum(WORK_TIME) as SUM_TIME from FIXBPA_FLOW_PROCESSINSTANCE where 1=1
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY) a
left join
(select PROCESSDEFINITION_KEY,count(1) AS TERMINATION_COUNT from FIXBPA_FLOW_PROCESSINSTANCE where INSTANCE_STATUS = 'TERMINATION'
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY) b on a.PROCESSDEFINITION_KEY=b.PROCESSDEFINITION_KEY left join
(select PROCESSDEFINITION_KEY,count(1) AS DONE_COUNT from FIXBPA_FLOW_PROCESSINSTANCE where INSTANCE_STATUS = 'COMPLETE'
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY) c on a.PROCESSDEFINITION_KEY=c.PROCESSDEFINITION_KEY
left join (select t1.PROCESS_KEY as PROCESSDEFINITION_KEY,t1.PROCESS_NAME from FIXBPA_FLOW_PROCESSDEFINITION t1 inner join ( select max(VERSION) as VERSION,PROCESS_KEY from FIXBPA_FLOW_PROCESSDEFINITION GROUP BY PROCESS_KEY ) t2 on t1.PROCESS_KEY=t2.PROCESS_KEY and t1.version=t2.version) d on a.PROCESSDEFINITION_KEY = d.PROCESSDEFINITION_KEY )
order by ${orderColumn} ${orderType},PROCESSDEFINITION_KEY ${orderType}

select count(1) AS PAGE_SIZE from (select count(1) from FIXBPA_FLOW_PROCESSINSTANCE where 1=1
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY,PROCESS_NAME) tb

select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME 
from (select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,sum(ALL_COUNT) as ALL_COUNT,sum(DONE_COUNT) as DONE_COUNT,sum (SUM_TIME) as SUM_TIME 
		from (select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT, sum(WORK_TIME) as SUM_TIME 
				from 
				  
			          
			            (select t2.*
							  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
							 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
				    		  
				        		and t1.START_TIME >= #{dateStart}
				    		
				    		  
			        			and t1.START_TIME <= #{dateEnd}
			    			
							 ) mt
			          
			          
			            fixbpa_flow_taskinstance
			          
			      
				where 1=1 
				  
	        		and PROCESSDEFINITION_KEY = #{procKey} 
	    		
	    		  
	        		and PROCESSDEFINITION_ID = #{procID} 
	    		
				group by PROCESSDEFINITION_ID,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME 
				union 
			   select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,NODE_ID,NODE_NAME,0 as ALL_COUNT, 0 as DONE_COUNT, 0 as SUM_TIME
				from FIXBPA_FLOW_NODE
				where 1=1 
				  
	        		and PROCESSDEFINITION_KEY = #{procKey} 
	    		
	    		  
	        		and PROCESSDEFINITION_ID = #{procID} 
	    		 ) 
 				group by PROCESSDEFINITION_ID,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) tb2

		select * from 
			(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME from
				(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT, sum(WORK_TIME) as SUM_TIME 
				from 
				  
			          
			            (select t2.*
							  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
							 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
				    		  
				        		and t1.START_TIME >= #{dateStart}
				    		
				    		  
			        			and t1.START_TIME <= #{dateEnd}
			    			
							 ) mt
			          
			          
			            fixbpa_flow_taskinstance
			          
			      
				where 1=1
				  
			    	and PROCESSDEFINITION_KEY = #{procKey}
			    
			      
			    	and PROCESSDEFINITION_ID = #{procID} 
			    
			      
			    	and NODE_ID = #{nodeID}
			    
			      
        			and USER_NAME like '%${userName}%' 
	    		
				group by PROCESSDEFINITION_ID,USER_NAME,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) tb ) tb2 
				where 1=1
				  
			    	and AVG_TIME >= #{secSumStart} and DONE_COUNT <> -1
			    
			      
			    	and AVG_TIME <= #{secSumEnd} and DONE_COUNT <> -1
			    
			      
			    	and DONE_COUNT >= #{doneCountStart}
			    
			      
			    	and DONE_COUNT <= #{doneCountEnd}
			    
			      
			    	and UNDONE_COUNT >= #{undoneCountStart}
			    
			      
			    	and UNDONE_COUNT <= #{undoneCountEnd}
			    
			    
				  
        			order by AVG_TIME ${timeOrder}
	    		
	    		  
        			order by DONE_COUNT ${doneOrder}
	    		
	    		  
        			order by UNDONE_COUNT ${undoneOrder}
	    		
	    		  
        			order by ALL_COUNT ${allOrder}

		select count(1) from 
			(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,DONE_COUNT,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME from 
				(select PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,USER_NAME,NODE_ID,NODE_NAME,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT, sum(WORK_TIME) as SUM_TIME 
				from 
				  
			          
			            (select t2.*
							  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
							 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
				    		  
				        		and t1.START_TIME >= #{dateStart}
				    		
				    		  
			        			and t1.START_TIME <= #{dateEnd}
			    			
							 ) mt
			          
			          
			            fixbpa_flow_taskinstance
			          
			      
				where 1=1
				  
			    	and PROCESSDEFINITION_KEY = #{procKey} 
			    
			      
			    	and PROCESSDEFINITION_ID = #{procID} 
			    
			      
			    	and NODE_ID = #{nodeID} 
			    
			      
        			and USER_NAME like '%${userName}%' 
	    		
				group by PROCESSDEFINITION_ID,USER_NAME,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) tb ) tb2 
				where 1=1
				  
			    	and AVG_TIME >= #{secSumStart} and DONE_COUNT <> -1
			    
			      
			    	and AVG_TIME <= #{secSumEnd} and DONE_COUNT <> -1
			    
			      
			    	and DONE_COUNT >= #{doneCountStart}
			    
			      
			    	and DONE_COUNT <= #{doneCountEnd}
			    
			      
			    	and UNDONE_COUNT >= #{undoneCountStart}
			    
			      
			    	and UNDONE_COUNT <= #{undoneCountEnd}

    	SELECT * FROM (SELECT PROCESSDEFINITION_KEY,USER_NAME,NODE_ID,NODE_NAME,ALL_COUNT,DONE_COUNT,(ALL_COUNT-DONE_COUNT) AS UNDONE_COUNT,CASE WHEN DONE_COUNT = 0 THEN -1 ELSE (SUM_TIME/DONE_COUNT) END AS AVG_TIME FROM 
        (SELECT PROCESSDEFINITION_KEY,USER_NAME,NODE_ID,NODE_NAME,COUNT(1) AS ALL_COUNT,COUNT(END_TIME) AS DONE_COUNT, SUM(WORK_TIME) AS SUM_TIME 
        FROM 
			  
		          
		            (select t2.*
						  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
						 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
			    		  
			        		and t1.START_TIME >= #{dateStart}
			    		
			    		  
		        			and t1.START_TIME <= #{dateEnd}
		    			
						 ) mt
		          
		          
		            fixbpa_flow_taskinstance
		          
		      
        WHERE 1=1
		
			and PROCESSDEFINITION_ID = #{procID}
			
		
			and PROCESSDEFINITION_KEY = #{procKey}
		
		
			and NODE_ID = #{nodeID}
		
        GROUP BY USER_NAME,PROCESSDEFINITION_KEY,NODE_ID,NODE_NAME) TB ) TB2 WHERE 1=1 AND USER_NAME IS NOT NULL
        
        	AND AVG_TIME >= 0
        
        ORDER BY ${orderColum} ${orderType}

    	SELECT  CASE WHEN DONE_COUNT = 0 THEN -1 ELSE (SUM_TIME/DONE_COUNT) END AS AVG_TIME,'平均处理时间' AS AVG_TIME_NAME FROM (SELECT PROCESSDEFINITION_KEY,COUNT(1) AS ALL_COUNT,COUNT(END_TIME) AS DONE_COUNT, SUM(WORK_TIME) AS SUM_TIME 
        FROM 
			  
		          
		            (select t2.*
						  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
						 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
			    		  
			        		and t1.START_TIME >= #{dateStart}
			    		
			    		  
		        			and t1.START_TIME <= #{dateEnd}
		    			
						 ) mt
		          
		          
		            fixbpa_flow_taskinstance
		          
		      
        WHERE 1=1
        
			and PROCESSDEFINITION_ID = #{procID}
			
		
			and PROCESSDEFINITION_KEY = #{procKey}
		
		
			and NODE_ID = #{nodeID}
		
        GROUP BY PROCESSDEFINITION_KEY)

    	SELECT SUM(DONE_COUNT)/COUNT(USER_NAME) AS AVG_DONE_COUNT, '平均完成次数' AS AVG_DONE_COUNT_NAME FROM (SELECT USER_NAME ,COUNT(END_TIME) DONE_COUNT
            FROM 
			  
		          
		            (select t2.*
						  from FIXBPA_FLOW_PROCESSINSTANCE t1, fixbpa_flow_taskinstance t2
						 where t1.PROCESSINSTANCE_ID = t2.PROCESSINSTANCE_ID
			    		  
			        		and t1.START_TIME >= #{dateStart}
			    		
			    		  
		        			and t1.START_TIME <= #{dateEnd}
		    			
						 ) mt
		          
		          
		            fixbpa_flow_taskinstance
		          
		      
            WHERE 1=1 
        
			and PROCESSDEFINITION_ID = #{procID}
			
		
			and PROCESSDEFINITION_KEY = #{procKey}
		
		
			and NODE_ID = #{nodeID}
		
        GROUP BY USER_NAME)

select a.PROCESSDEFINITION_KEY,d.PROCESS_NAME,ALL_COUNT, case when DONE_COUNT is null then 0 else DONE_COUNT end as DONE_COUNT ,(ALL_COUNT-DONE_COUNT) as UNDONE_COUNT,case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME,CASE WHEN TERMINATION_COUNT IS NULL THEN 0 ELSE TERMINATION_COUNT END AS TERMINATION_COUNT from ((select PROCESSDEFINITION_KEY,count(1) as ALL_COUNT,count(END_TIME) as DONE_COUNT,sum(WORK_TIME) as SUM_TIME from FIXBPA_FLOW_PROCESSINSTANCE where 1=1
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY) a
left join
(select PROCESSDEFINITION_KEY,count(1) AS TERMINATION_COUNT from FIXBPA_FLOW_PROCESSINSTANCE where INSTANCE_STATUS = 'TERMINATION'
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY) b on a.PROCESSDEFINITION_KEY=b.PROCESSDEFINITION_KEY left join
(select PROCESSDEFINITION_KEY,count(1) AS DONE_COUNT from FIXBPA_FLOW_PROCESSINSTANCE where INSTANCE_STATUS = 'COMPLETE'
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY) c on a.PROCESSDEFINITION_KEY=c.PROCESSDEFINITION_KEY
left join (select t1.PROCESS_KEY as PROCESSDEFINITION_KEY,t1.PROCESS_NAME from FIXBPA_FLOW_PROCESSDEFINITION t1 inner join ( select max(VERSION) as VERSION,PROCESS_KEY from FIXBPA_FLOW_PROCESSDEFINITION GROUP BY PROCESS_KEY ) t2 on t1.PROCESS_KEY=t2.PROCESS_KEY and t1.version=t2.version) d on a.PROCESSDEFINITION_KEY = d.PROCESSDEFINITION_KEY ) where DONE_COUNT<>0
ORDER BY ${orderColum} ${orderType},PROCESSDEFINITION_KEY ${orderType}

   	 select case when DONE_COUNT = 0 then -1 else (SUM_TIME/DONE_COUNT) end as AVG_TIME, '平均处理时间' AS AVG_TIME_NAME  
     from      
        (select count(END_TIME) as DONE_COUNT,sum(WORK_TIME) as SUM_TIME
          from FIXBPA_FLOW_PROCESSINSTANCE where 1=1
          
	   		and PROCESS_NAME like '%${procName}%' 
		
		  
	   		and START_TIME >= #{dateStart}
		
		  
			and START_TIME <= #{dateEnd}
		
          )

select SUM(DONE_COUNT)/COUNT(PROCESSDEFINITION_KEY) AS AVG_DONE_COUNT, '平均完成次数' AS AVG_DONE_COUNT_NAME from (select PROCESSDEFINITION_KEY,count(END_TIME) as DONE_COUNT,sum(WORK_TIME) as SUM_TIME from FIXBPA_FLOW_PROCESSINSTANCE where 1=1
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY)

		select PROCESSINSTANCE_ID,BIZ_KEY,PROCESSDEFINITION_KEY,PROCESS_NAME,SUBJECT,START_TIME,WORK_TIME,USER_NAME,END_TIME,INSTANCE_STATUS 

		from FIXBPA_FLOW_PROCESSINSTANCE
		where 1=1
		
			and BIZ_KEY like '%${procID}%' 
	  	 
	  	
			and PROCESS_NAME like '%${procName}%' 
	  	 
	  	
			and SUBJECT like '%${subject}%' 
	  	 
	  	  
    		and START_TIME >= #{dateStart}
		
		  
			and START_TIME <= #{dateEnd}
		
		
			and USER_NAME like '%${userName}%'
		
		
		  
			and INSTANCE_STATUS = #{instanceStatus}
		
	      
      		order by ${orderColumn} ${orderType}

select count(1) from FIXBPA_FLOW_PROCESSINSTANCE where 1=1
and BIZ_KEY like '%${procID}%'
and PROCESS_NAME like '%${procName}%'
and SUBJECT like '%${subject}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}

select NODE_ID,NODE_NAME,TASK_COMMENT,CREATE_TIME,END_TIME,WORK_TIME,USER_NAME,COMMAND_MESSAGE from FIXBPA_FLOW_TASKINSTANCE
where PROCESSINSTANCE_ID = #{procID}
order by CREATE_TIME

SELECT A.PROCESSDEFINITION_ID,A.PROCESSDEFINITION_KEY,A.PROCESS_NAME,ALL_COUNT,CASE WHEN DONE_COUNT IS NULL THEN 0 ELSE DONE_COUNT END AS DONE_COUNT,(ALL_COUNT-COMP_COUNT) AS UNDONE_COUNT,CASE WHEN DONE_COUNT = 0 THEN -1 WHEN DONE_COUNT IS NULL THEN -1 ELSE (SUM_TIME/DONE_COUNT) END AS AVG_TIME, CASE WHEN TERMINATION_COUNT IS NULL THEN 0 ELSE TERMINATION_COUNT END AS TERMINATION_COUNT FROM ((SELECT VERSION,PROCESSDEFINITION_ID,PROCESS_NAME,PROCESSDEFINITION_KEY,COUNT(1) AS ALL_COUNT,COUNT(END_TIME) AS COMP_COUNT,SUM(WORK_TIME) AS SUM_TIME FROM FIXBPA_FLOW_PROCESSINSTANCE WHERE 1=1
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
GROUP BY PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID,PROCESS_NAME,VERSION) A
LEFT JOIN
(SELECT PROCESSDEFINITION_ID,PROCESSDEFINITION_KEY,COUNT(1) AS TERMINATION_COUNT FROM FIXBPA_FLOW_PROCESSINSTANCE WHERE INSTANCE_STATUS = 'TERMINATION'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
GROUP BY PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID) B ON A.PROCESSDEFINITION_ID=B.PROCESSDEFINITION_ID LEFT JOIN
(SELECT PROCESSDEFINITION_ID,PROCESSDEFINITION_KEY,COUNT(1) AS DONE_COUNT FROM FIXBPA_FLOW_PROCESSINSTANCE WHERE INSTANCE_STATUS = 'COMPLETE'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
GROUP BY PROCESSDEFINITION_KEY,PROCESSDEFINITION_ID) C ON A.PROCESSDEFINITION_ID=C.PROCESSDEFINITION_ID ) WHERE 1=1
AND A.PROCESSDEFINITION_KEY = #{procKey}
ORDER BY A.VERSION DESC

select sum(TERMINATION_COUNT)/count(PROCESSDEFINITION_KEY) as AVG_TERMINATION_COUNT, '平均终止次数' AS AVG_TERMINATION_COUNT_NAME from ( select PROCESSDEFINITION_KEY,count(1) AS TERMINATION_COUNT from FIXBPA_FLOW_PROCESSINSTANCE where INSTANCE_STATUS = 'TERMINATION'
and PROCESS_NAME like '%${procName}%'
and START_TIME >= #{dateStart}
and START_TIME <= #{dateEnd}
group by PROCESSDEFINITION_KEY)
